import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import BobbyMouth from './components/BobbyMouth';
import {useSpeechSynthesis} from "react-speech-kit";

const mainText = "hello, world!"

function SpeechSynthesis() {


  let [value, setValue] = useState('');
  const { speak } = useSpeechSynthesis();

  function componentDidMount() {
    var btnInvis = document.getElementById("btnInvis")
    console.log("mounted")
    btnInvis.click()
  }

  return (
    <div style={{visibility: "hidden"}} id="divThingy">

      <textarea 
        value={value}
        onChange={(event) => setValue(event.target.value)}
      />

      <button onClick={() => {
        value = "Hello, World" 
        speak({text: value})}

        } id="btnInvis">Speak</button>
    </div>

  );

}


export default function App() {
  return (

    <div>
      <SpeechSynthesis></SpeechSynthesis>
      <BobbyMouth></BobbyMouth>


    </div>

  );

}

window.onload = () => {
  document.getElementById('btnInvis').click()
}
